import React, { Component } from "react";
import { Row, Col } from 'reactstrap';
import DateParser from "./DateParser";

class PostFactory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: this.props.postsToMap,
    };
  }

  render() {

    return(
      <Col xs={{size: 8}}>
        {this.state.posts.map(post =>
          <div className="content" key={this.post.ID}>
            <div className="post_heading_block">
              <h1>{this.post.title}</h1>
              <hr/>
              <Row className="post_author_block">
                <Col xs={{size: 3, offset: 2}}>
                  <img src={this.post.author.avatar_URL} alt={this.post.author.nice_name}/>
                </Col>
                <Col xs="6">
                  <h4>{this.post.author.first_name} {this.post.author.last_name}</h4>
                </Col>
              </Row>
              <hr/>
            </div>
            <div dangerouslySetInnerHTML = { {__html : this.post.content} }/>
            <hr/>
            <p>A post created <DateParser dateUTC={post.date} /></p>
          </div>
        )}
      </Col>
    );
  }
}

export default PostFactory;
