import React, { Component } from "react";
import { Container, Row, Col } from 'reactstrap';
import DateParser from "./DateParser";
import axios from "axios";

class PostById extends Component {
  constructor(props) {
    super(props);
    this.state = {
      post: null,
      postid: this.props.postId
    };
  }

  componentWillMount() {

    axios.get(`https://public-api.wordpress.com/rest/v1.1/sites/153341830/posts/36`)
    .then(response => { this.setState({ post:response.data }) });
    /*.then(data => this.setState({ post: data}));*/
  }

  render() {
    const { post } = this.state.post;

    if (post === null) {
      return <div>Loading..</div>;
    }

    return (
      <Col xs={{size: 8}}>
          <div className="content" key={post.ID}>
            <div className="post_heading_block">
              <h1>{post.title}</h1>
              <hr/>
              <Row className="post_author_block">
                <Col xs={{size: 3, offset: 2}}>
                  <img src={post.author.avatar_URL}/>
                </Col>
                <Col xs="6">
                  <h4><a href="#">{post.author.first_name} {post.author.last_name}</a></h4>
                </Col>
              </Row>
              <hr/>
            </div>
            <div dangerouslySetInnerHTML = { {__html : post.content} }/>
            <hr/>
            <p>A post created <DateParser dateUTC={post.date} /></p>
          </div>
      </Col>
    );
  }
}

export default PostById;
