import React, { Component } from "react";
import { Container, Row, Col } from 'reactstrap';
import DateParser from "./DateParser";

class GetReviews extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
    };
  }

  componentDidMount() {

    fetch('https://public-api.wordpress.com/rest/v1.1/sites/153341830/posts/?category=Reviews&tag=book')
    .then(results => {
      return results.json();
    })
    .then(data => this.setState({ posts: data.posts}));
  }

  render() {
    const { posts } = this.state;

    return (
      <Col xs={{size: 8}}>
        {posts.map(post =>
          <Col xs={{size:3}} className="reviewTile" key={post.ID}>
            <img src={post.featured_image}/>
          </Col>
        )}
      </Col>
    );
  }
}

export default GetReviews;
